body.script:
		$(function() {
			var sWidth = $("#focus").width(); //获取焦点图的宽度（显示面积）
			var len = $("#focus ul li").length; //获取焦点图个数
			var index = 0;
			var picTimer;
			
			//以下代码添加数字按钮和按钮后的半透明条，还有上一页、下一页两个按钮
			var btn = "
";
			for(var i=0; i < len; i++) {
				btn += "";
			}
			btn += "

";
			$("#focus").append(btn);
			
			
		
			//为小按钮添加鼠标滑入事件，以显示相应的内容
			$("#focus .btn span").css("opacity",0.4).mouseover(function() {
				index = $("#focus .btn span").index(this);
				showPics(index);
			}).eq(0).trigger("mouseover");
		
			//上一页、下一页按钮透明度处理
			$("#focus .preNext").css("opacity",0.2).hover(function() {
				$(this).stop(true,false).animate({"opacity":"0.5"},300);
			},function() {
				$(this).stop(true,false).animate({"opacity":"0.2"},300);
			});
		
			//上一页按钮
			$("#focus .pre").click(function() {
				index -= 1;
				if(index == -1) {index = len - 1;}
				showPics(index);
			});
		
			//下一页按钮
			$("#focus .next").click(function() {
				index += 1;
				if(index == len) {index = 0;}
				showPics(index);
			});
		
			//本例为左右滚动，即所有li元素都是在同一排向左浮动，所以这里需要计算出外围ul元素的宽度
			$("#focus ul").css("width",sWidth * (len));
			
			//鼠标滑上焦点图时停止自动播放，滑出时开始自动播放
			$("#focus").hover(function() {
				clearInterval(picTimer);
			},function() {
				picTimer = setInterval(function() {
					showPics(index);
					index++;
					if(index == len) {index = 0;}
				},4000); //此4000代表自动播放的间隔，单位：毫秒
			}).trigger("mouseleave");
			
			//显示图片函数，根据接收的index值显示相应的内容
			function showPics(index) { //普通切换
				var nowLeft = -index*sWidth; //根据index值计算ul元素的left值
				
				$("#focus ul").stop(true,false).animate({"left":nowLeft},0); //通过animate()调整ul元素滚动到计算出的position
				//$("#focus .btn span").removeClass("on").eq(index).addClass("on"); //为当前的按钮切换到选中的效果
				$("#focus .btn span").stop(true,false).animate({"opacity":"0.4"},0).eq(index).stop(true,false).animate({"opacity":"1"},0); //为当前的按钮切换到选中的效果
			}
		});

div.script:
$(function(){
	selectUnitMemberCol("weekCol");
	var so2 = new SWFObject("/amcharts/amcolumn.swf", "amcolumn", "680", "220", "8", "#FFFFFF");
	so2.addVariable("path", "/amcharts/");
	so2.addVariable("error_loading_file", "暂无统计数据"); 
	so2.addParam('wmode', 'Transparent'); 
	so2.addVariable("settings_file", encodeURIComponent("/amcharts/amcolumn_settings.xml"));
	so2.addVariable("data_file", encodeURIComponent("/amcharts/data1/corPartner_data.xml"));
	
	so2.addVariable("preloader_color", "#999999");
	so2.write("corPartner"); 
	var so3 = new SWFObject("/amcharts/amcolumn.swf", "amcolumn", "680", "220", "8", "#FFFFFF");
	so3.addVariable("path", "/amcharts/");
	so3.addVariable("error_loading_file", "暂无统计数据");  
	so3.addParam('wmode', 'Transparent'); 
	so3.addVariable("settings_file", encodeURIComponent("/amcharts/amcolumn_settings.xml"));
	so3.addVariable("data_file", encodeURIComponent("/amcharts/data1/personal_data.xml"));
	so3.addVariable("preloader_color", "#999999");
	so3.write("personal");
});
function selectUnitMemberCol(flag){
	$(".columnLink").removeClass("currentCol");
	$("#"+flag).addClass("currentCol");
	$.ajax({
	    type: "POST",
	    dataType:"html",
    	url: "/flaw/unitMemberCol",
    	data:"flag="+flag,
	    success: function(msg) {
		    if(msg != 'error') {
		    	$('#unitMemberColDiv').html(msg);
		    }
	    }
	});
}

body.script:
		$(function(){
			selectTrendCol("weekTrendCol");
			$(".hotLink").mouseover(function(){
				var href = $(this).attr('href');
				$(".hotLink").removeClass("current current1");
				if($(this).attr("id")=="hotRecommend"){
           			$(this).addClass("current1");
               	}else{
               		 $(this).addClass("current");
                }
				$(".hotDiv").hide();
				$("#"+$(this).attr("divId")).show();
				$(".hotLinkMore").attr("href",href);
			});
			$(".infoLink").mouseover(function(){
				var href = $(this).attr('href');
				$(".infoLink").removeClass("current current1");
				if($(this).attr("id")=="flawInfo"){
           			$(this).addClass("current1");
               	}else{
               		 $(this).addClass("current");
                }
				$(".infoDiv").hide();
				$("#"+$(this).attr("divId")).show();
				$(".infoLinkMore").attr("href",href);
			});
			$(".reportLink").mouseover(function(){
				$(".reportLink").removeClass("current current1");
				if($(this).attr("id")=="securityReport"){
           			$(this).addClass("current1");
               	}else{
               		 $(this).addClass("current");
                }
				$(".reportDiv").hide();
				$("#"+$(this).attr("divId")).show();
				$(".reportLinkMore").attr("href",$(this).attr("href"));				
			});
			var so4 = new SWFObject("/amcharts/ampie.swf", "ampie", "260", "180", "8", "#FFFFFF");
			so4.addVariable("path", "/amcharts/");
			 so4.addParam('wmode', 'Transparent');  
			so4.addVariable("error_loading_file", "暂无统计数据");  
			so4.addVariable("settings_file", encodeURIComponent("/amcharts/ampie_settings.xml"));
			so4.addVariable("data_file", encodeURIComponent("/amcharts/data1/ampie_data.txt"));
			so4.addVariable("preloader_color", "#999999");
			
			so4.write("weekSerietyLevelPie");
		});
		
		function selectTrendCol(flag){
			$(".trendColumnLink").removeClass("currentCol");
			$("#"+flag).addClass("currentCol");
			$.ajax({
			    type: "POST",
			    dataType:"html",
		    	url: "/flaw/trendCol",
		    	data:"flag="+flag,
			    success: function(msg) {
				    if(msg != 'error') {
				    	$('#trendColDiv').html(msg);
				    }
			    }
			});
